import React, { useState } from 'react';
import './Menu.css';
import Modal from './Modal.js';

// Swiper Carousel
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/pagination';
import { Pagination } from 'swiper/modules';

// Images
import breakfastImg1 from '../images/breakfast-1.jpg';
import breakfastImg2 from '../images/breakfast-2.jpg';
import lunchImg1 from '../images/lunch-1.jpg';
import lunchImg2 from '../images/lunch-2.jpg';
import dinnerImg1 from '../images/dinner-1.jpg';
import dinnerImg2 from '../images/dinner-2.jpg';
import drinksImg1 from '../images/drinks-1.jpg';
import drinksImg2 from '../images/drinks-2.jpg';

const menuData = [
  {
    title: "Breakfast",
    images: [breakfastImg1, breakfastImg2],
    timing: "8:00 AM - 11:00 AM",
    items: ["Pancakes", "Omelette", "Idli", "Poha"]
  },
  {
    title: "Lunch",
    images: [lunchImg1, lunchImg2],
    timing: "12:00 PM - 3:00 PM",
    items: ["Chicken Curry", "Paneer Butter Masala", "Roti", "Rice"]
  },
  {
    title: "Dinner",
    images: [dinnerImg1, dinnerImg2],
    timing: "7:00 PM - 10:00 PM",
    items: ["Biryani", "Dal Makhani", "Salad", "Naan"]
  },
  {
    title: "Drinks",
    images: [drinksImg1, drinksImg2],
    timing: "All Day",
    items: ["Lassi", "Soda", "Mocktail", "Tea"]
  },
];

function Menu() {
  const [selectedMenu, setSelectedMenu] = useState(null);

  const openModal = (menu) => setSelectedMenu(menu);
  const closeModal = () => setSelectedMenu(null);

  return (
    <div className="menu-container" id="menu">
      <h2 className="menu-title">Menu Items</h2>
      <div className="menu-grid">
        {menuData.map((menu, index) => (
          <div key={index} className="menu-card">
            <Swiper
              pagination={{ clickable: true }}
              modules={[Pagination]}
              className="menu-swiper"
            >
              {menu.images.map((image, i) => (
                <SwiperSlide key={i}>
                  <img src={image} alt={`${menu.title} ${i}`} className="menu-img" />
                </SwiperSlide>
              ))}
            </Swiper>
            <h3>{menu.title}</h3>
            <p>{menu.timing}</p>
            <button onClick={() => openModal(menu)}>View Items</button>
          </div>
        ))}
      </div>
      {selectedMenu && <Modal menu={selectedMenu} onClose={closeModal} />}
    </div>
  );
}

export default Menu;
