import React, { useState } from 'react';
import './Menu.css';

function Modal({ menu, onClose, onPlaceOrder }) {
  const [selectedItems, setSelectedItems] = useState([]);
  const [deliveryType, setDeliveryType] = useState('');
  const [tableNumber, setTableNumber] = useState('');

  const toggleItem = (item) => {
    setSelectedItems((prev) =>
      prev.includes(item)
        ? prev.filter(i => i !== item)
        : [...prev, item]
    );
  };

  const handleOrder = () => {
    if (!deliveryType) return alert("Please select delivery type");
    if (deliveryType === "restaurant" && !tableNumber)
      return alert("Please enter table number");

    const orderDetails = {
      items: selectedItems,
      deliveryType,
      tableNumber: deliveryType === "restaurant" ? tableNumber : null,
      category: menu.title,
    };

    onPlaceOrder(orderDetails);
    onClose(); // close modal after placing order
  };

  return (
    <div className="modal-overlay">
      <div className="modal-box">
        <h2>{menu.title} Items</h2>
        <div className="modal-items">
          {menu.items.map((item, i) => (
            <div
              key={i}
              className={`modal-card ${selectedItems.includes(item) ? 'selected' : ''}`}
              onClick={() => toggleItem(item)}
            >
              {item}
            </div>
          ))}
        </div>

        {/* Selected Items Preview */}
        {selectedItems.length > 0 && (
          <div className="selected-preview">
            <h4>Selected Items:</h4>
            <ul>
              {selectedItems.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="order-section">
          <h4>Select Delivery Type:</h4>
          <label>
            <input
              type="radio"
              name="delivery"
              value="restaurant"
              onChange={(e) => setDeliveryType(e.target.value)}
            /> In Restaurant
          </label>
          <label>
            <input
              type="radio"
              name="delivery"
              value="home"
              onChange={(e) => setDeliveryType(e.target.value)}
            /> Home Delivery
          </label>

          {deliveryType === 'restaurant' && (
            <input
              type="number"
              placeholder="Enter Table Number"
              value={tableNumber}
              onChange={(e) => setTableNumber(e.target.value)}
            />
          )}

          <button
            className="order-btn"
            onClick={handleOrder}
            disabled={selectedItems.length === 0}
          >
            Place Order
          </button>
        </div>

        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
}

export default Modal;
